80         template<typename T, typename... Args>
81         T& cScene::AddComponent(EntityID entityId, Args&&... args)
82         {
83             T* component;
84 
85             if constexpr(sizeof...(Args) != 0u) {
86                 component = &m_Registry.emplace_or_replace<T>(entityId, std::forward<Args>(args)...);
87             }
88             else {
89                 component = &m_Registry.emplace_or_replace<T>(entityId, T());
90             }
91 
92             if constexpr (std::is_base_of<cJson, T>::value) {
93                 m_Jsons[entityId][typeid(T).hash_code()] = component;
94             }
95             else if constexpr (std::is_base_of<cXml, T>::value) {
96                 m_Xmls[entityId][typeid(T).hash_code()] = component;
97             }
98 
99             return *component;
100         }
101 
102         template<typename T>
103         void cScene::RemoveComponent(EntityID entityId)
104         {
105             if (std::is_base_of<cJson, T>::value) {
106                 m_Jsons[entityId].erase(typeid(T).hash_code());
107             }
108             else if (std::is_base_of<cXml, T>::value) {
109                 m_Xmls[entityId].erase(typeid(T).hash_code());
110             }
111 
112             if (m_Registry.any_of<T>(entityId))
113             {
114                 m_Registry.erase<T>(entityId);
115             }
116         }
117 
118         template<typename T>
119         T& cScene::GetComponent(EntityID entityId)
120         {
121             return m_Registry.get<T>(entityId);
122         }
123 
124         template<typename... T>
125         bool cScene::HasAnyComponent(EntityID entityId)
126         {
127             return m_Registry.any_of<T...>(entityId);
128         }
129 
130         template<typename... T>
131         bool cScene::HasAllComponent(EntityID entityId)
132         {
133             return m_Registry.all_of<T...>(entityId);
134         }
135 
136         template<typename... T>
137         void cScene::ClearComponents()
138         {
139             m_Registry.clear<T...>();
140             m_Jsons.clear();
141             m_Xmls.clear();
142         }
143 
144         template<typename... T>
145         auto cScene::GetComponents()
146         {
147             return m_Registry.view<T...>();
148         }
